import React, { useState } from 'react';

function isPrime(n) {
  // Проверяем, является ли число меньше или равно 1
  if (n <= 1) {
      return false;
  }
  // Проверяем делители от 2 до квадратного корня из n
  for (let i = 2; i <= Math.sqrt(n); i++) {
      if (n % i === 0) {
          return false; // Найден делитель, число не простое
      }
  }
  return true; // Число простое
}
function getRandomNumber(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

const App = () => {
    const [p, setP] = useState(0);
    const [q, setQ] = useState(0);
    const [publicKey, setPublicKey] = useState('');
    const [privateKey, setPrivateKey] = useState('');
    const [loading, setLoading] = useState(false);

    const handleGenerateKeys = async () => {
        setLoading(true);
        let pValue=8;
        while (!isPrime(pValue)){
        pValue=getRandomNumber(1,1000000000000000000000000)
        }
        let qValue=8;
        while (!isPrime(qValue)){
        qValue=getRandomNumber(1,10000000000000000000000)
        }
        setP(pValue);
        setQ(qValue);
        setPublicKey(pValue);
        setPrivateKey(qValue);
        setLoading(false);
    };

    return (
        <div>
            <h1>Генератор ключей RSA</h1>
            <button onClick={handleGenerateKeys} disabled={loading}>
                {loading ? 'Генерация...' : 'Сгенерировать ключи'}
            </button>
            <h2>Случайные простые числа:</h2>
            <p>p: {p}</p>
            <p>q: {q}</p>
            <h2>Публичный ключ:</h2>
            <p>{publicKey}</p>
            <h2>Приватный ключ:</h2>
            <p>{privateKey}</p>
        </div>
    );
};

export default App;
