import React, { useState } from 'react';

const isPrime = (n) => {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 === 0) return false;

    for (let i = 3; i * i <= n; i += 2) {
        if (n % i === 0) return false;
    }
    return true;
};

const getRandomInt = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

const getRandomPrime = () => {
    let num;
    do {
        num = getRandomInt(1, 1e6); // Генерация случайного числа от 1 до 1 000 000
    } while (!isPrime(num));
    return num;
};

const generateKeys = () => {
    return new Promise((resolve) => {
        const pValue = getRandomPrime();
        const qValue = getRandomPrime();

        // Вычисление n = p * q
        const n = pValue * qValue;

        // Вычисление φ(n) = (p-1) * (q-1)
        const phi = (pValue - 1) * (qValue - 1);

        // Генерация публичного и приватного ключей (для простоты, пусть e = 3)
        const e = 3;
        let d = 0;

        // Находим d так, чтобы (d * e) mod φ(n) = 1
        for (let i = 1; ; i++) {
            if ((e * i) % phi === 1) {
                d = i;
                break;
            }
        }

        resolve({ pValue, qValue, publicKey: `(${e}, ${n})`, privateKey: `(${d}, ${n})` });
    });
};

const App = () => {
    const [p, setP] = useState(0);
    const [q, setQ] = useState(0);
    const [publicKey, setPublicKey] = useState('');
    const [privateKey, setPrivateKey] = useState('');
    const [loading, setLoading] = useState(false);

    const handleGenerateKeys = async () => {
        setLoading(true);
        const { pValue, qValue, publicKey, privateKey } = await generateKeys();
        setP(pValue);
        setQ(qValue);
        setPublicKey(publicKey);
        setPrivateKey(privateKey);
        setLoading(false);
    };

    return (
        <div>
            <h1>Генератор ключей RSA</h1>
            <button onClick={handleGenerateKeys} disabled={loading}>
                {loading ? 'Генерация...' : 'Сгенерировать ключи'}
            </button>
            <h2>Случайные простые числа:</h2>
            <p>p: {p}</p>
            <p>q: {q}</p>
            <h2>Публичный ключ:</h2>
            <p>{publicKey}</p>
            <h2>Приватный ключ:</h2>
            <p>{privateKey}</p>
        </div>
    );
};

export default App;
